import { DAYS } from "./const";
import { Wrapper, CalendarHead, SevenColGrid, HeadDay, CalendarBody, StyledDay } from "./mes-styles"
import { range } from "./utils";



export const Calendar = () => {
    return (
        <Wrapper>
            <CalendarHead>
                <ion-icon name="arrow-back-outline"></ion-icon>
                <p>Oct 2022></p>
                <ion-icon name="arrow-forward-outline"></ion-icon>
            </CalendarHead>
            <SevenColGrid>
                {DAYS.map((day) => (
                    <HeadDay>{day}</HeadDay>
                ))}
                </SevenColGrid>
                <CalendarBody>
                    {range(31).map((day) => (
                        <StyledDay>{day}</StyledDay>
                    ))}
                </CalendarBody>
            </Wrapper>      
    );
}
