import React, { useState } from 'react';
import { DAYS, MONTHS } from "./const";
import { Wrapper, CalendarHead, SevenColGrid, HeadDay, CalendarBody, StyledDay } from "./mes-styles"
import { range } from "./utils";



export const Calendar = ({startingDate}) => {
    const [currentMonth, setCurrentMonth] = useState(startingDate.getMonth())
    const [currentYear, setCurrentYear] = useState(startingDate.getFullYear())

    return (
        <Wrapper>
            <CalendarHead>
                <ion-icon name="arrow-back-outline"></ion-icon>
                <p>{MONTHS[currentMonth]} {currentYear}</p>
                <ion-icon name="arrow-forward-outline"></ion-icon>
            </CalendarHead>
            <SevenColGrid>
                {DAYS.map((day) => (
                    <HeadDay>{day}</HeadDay>
                ))}
                </SevenColGrid>
                <CalendarBody>
                    {range(getDaysInMonth(currentMonth, currentYear)).map((day) => (
                        <StyledDay>{day}</StyledDay>
                    ))}
                </CalendarBody>
            </Wrapper>      
    );
}
